import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Menu, X } from "lucide-react";

const Header = () => {
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false); // State for mobile menu

  return (
    <header className="w-full bg-white shadow-md">
      {/* Navbar */}
      <nav className="flex justify-between items-center px-6 py-4 md:px-10">
        {/* Logo (Now Clickable) */}
        <Link to="/" className="text-2xl font-bold text-green-600">
          PlantKart
        </Link>

        {/* Desktop Navigation */}
        <ul className="hidden md:flex space-x-6">
          {[
            { name: "Home", path: "/home" },
            { name: "Plants", path: "/plants" },
            { name: "Planters", path: "/planters" },
            { name: "Essentials", path: "/essentials" },
            { name: "Services", path: "/services" },
            { name: "Shop", path: "/shop" },
          ].map((item) => (
            <li key={item.name}>
              <Link
                to={item.path}
                className="text-gray-700 hover:text-green-600 transition"
              >
                {item.name}
              </Link>
            </li>
          ))}
        </ul>

        {/* Search & Icons */}
        <div className="flex items-center space-x-4">
          <input
            type="text"
            placeholder="Search"
            className="hidden md:block px-3 py-1 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-400"
          />
          <button className="text-xl text-gray-600 hover:text-green-600 transition">
            ðŸ›’
          </button>
          <button
            className="text-xl text-gray-600 hover:text-green-600 transition"
            onClick={() => navigate("/login")}
          >
            ðŸ‘¤
          </button>
          {/* Hamburger Menu Button for Mobile */}
          <button
            className="md:hidden text-gray-700 focus:outline-none"
            onClick={() => setMenuOpen(!menuOpen)}
          >
            {menuOpen ? <X size={28} /> : <Menu size={28} />}
          </button>
        </div>
      </nav>

      {/* Mobile Menu */}
      {menuOpen && (
        <div className="md:hidden flex flex-col items-center bg-white py-4 shadow-md">
          {[
            { name: "Home", path: "/home" },
            { name: "Plants", path: "/plants" },
            { name: "Planters", path: "/planters" },
            { name: "Essentials", path: "/essentials" },
            { name: "Services", path: "/services" },
            { name: "Shop", path: "/shop" },
          ].map((item) => (
            <Link
              key={item.name}
              to={item.path}
              className="py-2 text-gray-700 hover:text-green-600 transition"
              onClick={() => setMenuOpen(false)}
            >
              {item.name}
            </Link>
          ))}
        </div>
      )}

      {/* Background Section */}
      <div className="bg-green-100 py-16 text-center">
        <h1 className="text-4xl font-bold text-green-700">Plan a Plant</h1>
      </div>
    </header>
  );
};

export default Header;
