import React from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate

const categories = [
  {
    name: "BONSAI",
    img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQtcN68RmeyZMh9hLDW_Lci0KaIPVZZjxy4mQ&s",
  },
  {
    name: "CACTUS",
    img: "https://media.istockphoto.com/id/1056600248/photo/house-indoor-plants-collection-succulent-and-cactus-in-different-pots-on-white-background.jpg?s=612x612&w=0&k=20&c=VaPeEhKGPSihTmgsZXSv5z_k5s6kIe59V3zrNf8bIYQ=",
  },
  {
    name: "CREEPERS",
    img: "https://m.media-amazon.com/images/I/61C613Fu+GL._AC_UF1000,1000_QL80_.jpg",
  },
  {
    name: "SUCCULENTS",
    img: "https://www.moonvalleynurseries.com/_next/image?url=https%3A%2F%2Fcdn.mvncorp.dev%2Fmedia%2Fheroes%2Flarge%2FMVN-Succulents-Hero.jpeg&w=3840&q=50",
  },
  {
    name: "SEEDS",
    img: "https://www.anneofgreengardens.com/wp-content/uploads/2015/08/Seeds-Anne-of-Green-Gardens.jpg",
  },
  {
    name: "GIFTING",
    img: "https://www.angroos.com/wp-content/uploads/2023/07/marriage-return-gift-ideas.jpg",
  },
];

const HeroSection = () => {
  const navigate = useNavigate(); // Hook for navigation

  // Navigate to the shop page with category as a query parameter
  const handleNavigate = (category) => {
    navigate(`/shop?category=${encodeURIComponent(category)}`);
  };

  return (
    <section className="py-10 bg-gray-100">
      {/* Hero Banner (If needed) */}
      {/* <div className="h-[300px] bg-cover bg-center bg-green-500 flex items-center justify-center text-white text-3xl font-bold">
        Welcome to Our Plant Store ðŸŒ¿
      </div> */}

      {/* Categories */}
      <div className="max-w-6xl mx-auto mt-8 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-3 gap-6 px-4">
        {categories.map((category, index) => (
          <div
            key={index}
            className="relative bg-white rounded-lg shadow-md overflow-hidden transform transition duration-300 hover:scale-105 cursor-pointer h-[400px]"
            onClick={() => handleNavigate(category.name)}
          >
            {/* Category Image */}
            <img
              src={category.img}
              alt={category.name}
              className="w-full h-full object-cover"
            />
            {/* Overlay */}
            <div className="absolute inset-0 bg-black bg-opacity-40 flex flex-col justify-center items-center opacity-0 hover:opacity-100 transition duration-300">
              <p className="text-white text-lg font-semibold">
                {category.name}
              </p>
            </div>
          </div>
        ))}
      </div>

      {/* "See All" Button */}
      <div className="text-center mt-8">
        <a
          href="/shop"
          className="text-green-600 font-semibold text-lg hover:underline"
          onClick={(e) => e.stopPropagation()} // Prevent click event bubbling
        >
          See all â†’
        </a>
      </div>
    </section>
  );
};

export default HeroSection;
